<template>
    <div>
        <form @submit.prevent="handleSubmit">
            <h2>Login</h2>
            <p>New User? <a><router-link to="/register">Create an account</router-link></a></p>
            <div class="form-group">
                <label for="username">Username</label>
                <input type="text" v-model="username" name="username" class="form-control" :class="{ 'is-invalid': submitted && !username }" />
                <div v-show="submitted && !username" class="invalid-feedback">Username is required</div>
            </div>
            <div class="form-group">
                <label htmlFor="password">Password</label>
                <input type="password" v-model="password" name="password" class="form-control" :class="{ 'is-invalid': submitted && !password }" />
                <div v-show="submitted && !password" class="invalid-feedback">Password is required</div>
            </div>
            <div class="form-group">
                <button class="btn btn-primary" :disabled="status.loggingIn">Login</button>
                <img v-show="status.loggingIn" src="data:image/gif;base64," />
            </div>
        </form>
    </div>
</template>

<script>
import { mapState, mapActions } from 'vuex'

export default {
    data () {
        return {
            username: '',
            password: '',
            submitted: false
        }
    },
    computed: {
        ...mapState('account', ['status'])
    },
    created () {
        // reset login status
        this.logout();
    },
    methods: {
        ...mapActions('account', ['login', 'logout']),
        handleSubmit (e) {
            this.submitted = true;
            const { username, password } = this;
            if (username && password) {
                this.login({ username, password })
            }
        }
    }
};
</script>

<style scoped>
/* Set a style for all buttons */
button {
  background-color: #04AA6D;
  color: white;
  padding: 14px 20px;
  margin: 8px 0;
  border: none;
  cursor: pointer;
  width: 100%;
}
.btn1{
  font-size: 18px;
  color:rgb(255, 255, 255);
  cursor: pointer;
  padding: 10px;
  width:60px;
  text-align: center;
}
.button2
{
  color:white;
  background: rgba(255, 0, 0, 0.76);
  margin:10px;
  margin-top: 7px;
  margin-bottom: 7px;
  text-align: center;
  margin-left: 1000px
}
form {
  border: 3px solid #f1f1f1;
  margin-top: 20px;
}
a {
  color: #ff0000af;
}
</style>